import PauseButton from "./pauseButton"
import PlayButton from "./playButton"
import ResumeButton from "./resumeButton"

import { useEffect, useState } from "react"


function MusicControls(props) {


    // next and previous logic for songs
    const [songArrayNum, setSongArrayNumber] = useState(0)

    function nextSongHandler() {
        setSongArrayNumber(songArrayNum + 1)
    }

    function prevSongHandler() {
        setSongArrayNumber(songArrayNum - 1)
    }

    // let currentSong = musicArrayList[songArrayNum]

    const [currentSongInfo, setCurrentSongInfo] = useState({})


    const [currentSong, setCurrentSong] = useState()


    console.log("print props for music controls")

    console.log(props)
    let musicArrayList = props.songList


    // let currentSong = props.songList[songArrayNum]

    console.log("controls array")
    console.log(musicArrayList)

    // let numSongsAvailable = musicArrayList.length

    // console.log(`current music control list is ${numSongsAvailable} songs long`)



    // const [nowSong, setNowSong] = useState("now song")

    function updateSongInfo(info) {
        setCurrentSongInfo(
            () => {
                currentSongInfo.name = currentSong.track.name
                currentSongInfo.uri = currentSong.track.uri
                currentSongInfo.description = currentSong.track.description
                currentSongInfo.href = currentSong.track.href
                currentSongInfo.id = currentSong.track.id

                console.log("NEW UPDATE ON SONG", currentSongInfo)
                return currentSongInfo
            }
        )
    }


    function songMaker(song) {
        console.log("song maker before setting song")
        console.log(song)
        setCurrentSong(
            () => song
        )
        console.log("currentsong after set")
        console.log(currentSong)
        console.log(currentSong)

        // console.log("current song on placement change")
        // console.log(currentSong[songArrayNum])
    }



    useEffect(
        () => {
            if (!props.songList) return


            songMaker(props.songList[songArrayNum])
            updateSongInfo(currentSong)
        }, [currentSong]
    )


    console.log("data from current song in song conrols")

    // console.log(props.songList[1])
    // console.log(`name of song playing is ${props.songList[1].track.name}`)


    // useEffect(
    //     () => {
    //         songMaker()
    //     }
    //     , [songArrayNum]
    // )


    // let content = "none"

    // if (!props.songList[songArrayNum]) {
    //     content = "broken song array num"
    // } else {
    //     content = "working song array"
    // }


    return (
        <div className="bg-info m-3 p-3">
            <div>
                music controls
                <div>
                    <div>
                    </div>
                    <div>
                        {songArrayNum > 0 &&
                            <button className="btn btn-outline-light" onClick={prevSongHandler}>prev track</button>
                        }

                        {
                            songArrayNum < 0 &&
                            <button className="btn btn-outline-light" onClick={nextSongHandler}>next track</button>
                        }
                    </div>
                    <PlayButton currentSongCode={"song"} />
                    <PauseButton />
                    <ResumeButton />
                </div>
            </div>
        </div>
    )
}


export default MusicControls
