import { useEffect, useState } from "react"
const axios = require('axios')





function Soundbar(props) {

    const [songAudioLevel, setAudioLevel] = useState(75)

    function volumeUp() {
        let level = parseInt(songAudioLevel)
        setAudioLevel(level)
    }

    return (
        <div>
            <button className="m-1 btn btn-light">-</button>
            <div className="w-50">
                <label for="musicAudio" class="form-label">Example range</label>
                <input type="range" class="form-range" min="0" max="100" step="20" id="musicAudio" />
                <span>Speaker {songAudioLevel}</span>
            </div>
            <button className="m-1 btn btn-light">+</button>
        </div>
    )

}

export default Soundbar
