import { useEffect, useState } from "react"
const axios = require('axios')





function Soundbar(props) {

    const [songAudioLevel, setAudioLevel] = useState(70)

    function volumeUp() {
        let level = parseInt(songAudioLevel)
        setAudioLevel(level)
    }

    return (
        <div className="w-75">
            <div className="w-50">
                <button className="m-1 btn btn-light">-</button>
                <div>
                    <label for="musicAudio" className="form-label">Speaker {songAudioLevel}</label>
                    <input
                        type="range"
                        className="form-range"
                        onChange={(e) => { setAudioLevel(e.target.value) }}
                        id="musicAudio"
                        value={songAudioLevel}
                        min="0"
                        max="100"
                        step="10"

                    />
                </div>
                <button className="m-1 btn btn-light">+</button>
            </div>
        </div>
    )

}

export default Soundbar
