const axios = require('axios')


function PlayButton(props) {

    let token = localStorage.getItem("spotifyToken")

    let musicList = props.tracks
    let musicTracks = musicList.map(
        tracks => {
            return tracks.track.id
        }
    )

    console.log(musicTracks)


    function playButtonHandler() {

        console.log("playing music")
        axios.post('/spotify-play-music', {
            userData: {
                accessToken: `${token}`,
                musicURI: props.playlistInfo.tracks
            }
        }).then(
            (res) => {
                console.log("play worked")
                console.log(res)
            }
        ).catch(
            (err) => {
                console.log(err)
            }
        )

    }

    let words = "play music"


    return (

        <div>
            <button onClick={playButtonHandler}>{words}</button>
        </div>

    )
}




export default PlayButton
