import React, { Fragment, useEffect } from "react";
import { useState } from "react"
import MusicControls from "./musicControls";

import SongCard from "./songCard"



function SongSection(props) {



    const [arrayTracks, setArrayTracks] = useState([])


    // replace props.whatever.we to correct data format
    // replace this too
    let musicInfo = props.playlistInfo
    let musicList = musicInfo.tracks

    console.log("printing array of tracks in song section")
    console.log(musicList)



    // if the prop exists, loop through each prop and move it into the tracksArray array
    if (musicList) {
        setArrayTracks(
            musicList.forEach(
                el => arrayTracks.push(el)
            )
        )
        console.log("tracks array")
        console.log(arrayTracks)
    }




    let content = "songList"

    if (arrayTracks.length < 5) {
        content = "broken"
    } else {
        content = "not broken"
    }
    console.log("tracks data from playlist", arrayTracks)


    // const content = songsLoading ?
    //     (<div>...is Loading</div>)
    //     :
    //     (<div> Loading Complete
    //     </div>)



    return (
        <div>
            <div>
                {content}
            </div>
            <MusicControls songList={arrayTracks} />
            {
                arrayTracks.map(
                    (song, index) => (
                        <SongCard
                            uri={song.track.uri}
                            name={song.track.name}
                            // album={song.track.album.name}
                            artist={song.track.artists[0].name}
                            id={song.track.id}
                            href={song.track.href}
                        />
                    )
                )
            }
        </div>
    )
}

export default SongSection
