import React, { Fragment, useEffect } from "react";
import { useState } from "react"
import MusicControls from "./musicControls";

import SongCard from "./songCard"



function SongSection(props) {

    let musicInfo = props.songs

    let newArray = []

    // const [arrayTracks, setArrayTracks] = useState(newArray)

    let musicList = [musicInfo.track]

    console.log("printing array of tracks in song section")
    console.log(musicList)



    // if the prop exists, loop through each prop and move it into the tracksArray array
    if (musicList) {
        musicList.forEach(
            el => newArray.push(el)
        )
        // setArrayTracks(
        // )

        console.log("tracks array")
        console.log(newArray)
        return newArray
    }




    let content = "songList"

    if (newArray.length < 5) {
        content = "broken"
    } else {
        content = "not broken"
    }
    console.log("tracks data from playlist", newArray)


    // const content = songsLoading ?
    //     (<div>...is Loading</div>)
    //     :
    //     (<div> Loading Complete
    //     </div>)



    return (
        <div>
            <div>
                {content}
            </div>
            <MusicControls songList={newArray} />
            {
                newArray.map(
                    (song, index) => (
                        <SongCard
                            uri={song.track.uri}
                            name={song.track.name}
                            // album={song.track.album.name}
                            artist={song.track.artists[0].name}
                            id={song.track.id}
                            href={song.track.href}
                        />
                    )
                )
            }
        </div>
    )
}

export default SongSection
